sosh_js = function(runBeforeShow) {

    /* Object & array with components "name-to-id" mapping */
    var n2id_buf = {
        'mobileimage_24': 'sosh_mobileimage_24',
        'mobilegrid_1': 'sosh_mobilegrid_1',
        'mobilegridcell_2': 'sosh_mobilegridcell_2',
        'mobileimage_6': 'sosh_mobileimage_6',
        'mobilegridcell_3': 'sosh_mobilegridcell_3',
        'mobileimage_7': 'sosh_mobileimage_7',
        'mobilegridcell_4': 'sosh_mobilegridcell_4',
        'mobileimage_8': 'sosh_mobileimage_8',
        'mobilegridcell_5': 'sosh_mobilegridcell_5',
        'mobileimage_15': 'sosh_mobileimage_15',
        'mobilegridcell_9': 'sosh_mobilegridcell_9',
        'mobileimage_16': 'sosh_mobileimage_16',
        'mobilegridcell_10': 'sosh_mobilegridcell_10',
        'mobileimage_17': 'sosh_mobileimage_17',
        'mobilegridcell_11': 'sosh_mobilegridcell_11',
        'mobileimage_18': 'sosh_mobileimage_18',
        'mobilegridcell_12': 'sosh_mobilegridcell_12',
        'mobileimage_19': 'sosh_mobileimage_19',
        'mobilegridcell_13': 'sosh_mobilegridcell_13',
        'mobileimage_20': 'sosh_mobileimage_20',
        'mobilegridcell_14': 'sosh_mobilegridcell_14',
        'mobileimage_22': 'sosh_mobileimage_22'
    };

    if ("n2id" in window && window.n2id !== undefined) {
        $.extend(n2id, n2id_buf);
    } else {
        window.n2id = n2id_buf;
    }

    if (navigator.userAgent.indexOf("IEMobile") != -1) {
        //Fix for jQuery Mobile's footer in Cordova webview on WP8 devices
        var msViewportStyle = document.createElement("style");
        msViewportStyle.appendChild(
        document.createTextNode("@media screen and (orientation: portrait){@-ms-viewport {width: 320px; height: 536px;user-zoom: fixed;max-zoom: 1;min-zoom: 1;}}" + "@media screen and (orientation:landscape){@-ms-viewport{width:480px;user-zoom:fixed;max-zoom:1;min-zoom:1;}}"));
        document.getElementsByTagName("head")[0].appendChild(msViewportStyle);
    }

    Apperyio.CurrentScreen = 'sosh';

    /*
     * Nonvisual components
     */
    var datasources = [];

    /*
     * Events and handlers
     */

    // Before Show
    var sosh_beforeshow = function() {
            Apperyio.CurrentScreen = "sosh";
            for (var idx = 0; idx < datasources.length; idx++) {
                datasources[idx].__setupDisplay();
            }
        };

    // On Load
    var sosh_onLoad = function() {
            sosh_elementsExtraJS();

            // TODO fire device events only if necessary (with JS logic)
            sosh_deviceEvents();
            sosh_windowEvents();
            sosh_elementsEvents();
        };

    // screen window events
    var sosh_windowEvents = function() {

            $('#sosh').bind('pageshow orientationchange', function() {
                var _page = this;
                adjustContentHeightWithPadding(_page);
            });

        };

    // device events
    var sosh_deviceEvents = function() {
            document.addEventListener("deviceready", function() {

            });
        };

    // screen elements extra js
    var sosh_elementsExtraJS = function() {
            // screen (sosh) extra code

        };

    // screen elements handler
    var sosh_elementsEvents = function() {
            $(document).on("click", "a :input,a a,a fieldset label", function(event) {
                event.stopPropagation();
            });

            $(document).off("click", '#sosh_mobilecontainer [name="mobileimage_6"]').on({
                click: function() {
                    if (!$(this).attr('disabled')) {
                        Apperyio.navigateTo('sosh_2', {
                            transition: 'none',
                            reverse: false
                        });

                    }
                },
            }, '#sosh_mobilecontainer [name="mobileimage_6"]');

        };

    $(document).off("pagebeforeshow", "#sosh").on("pagebeforeshow", "#sosh", function(event, ui) {
        sosh_beforeshow();
    });

    if (runBeforeShow) {
        sosh_beforeshow();
    } else {
        sosh_onLoad();
    }
};

$(document).off("pagecreate", "#sosh").on("pagecreate", "#sosh", function(event, ui) {
    Apperyio.processSelectMenu($(this));
    sosh_js();
});